angular.module("flowerStore").service("FlowerService",function(r,e,n,t){var o=this;o.fetchFlowers=function(){return r.get(t.api_url+"/flowers").then(function(r){return r.data})},o.fetchFlower=function(e){return r.get(t.api_url+"/flower/"+e).then(function(r){return r.data})},o.randomFlowers=function(){return r.get(t.api_url+"/random_flowers/").then(function(r){return r.data})}});